import SwiftUI

struct ContentView: View {
    
    @ObservedObject var viewModel : MemorizeViewModel
    
    var body: some View {
        NavigationView {
            VStack {
                ScrollView{
                    LazyVGrid (columns: [GridItem(.adaptive(minimum: 70))]){
                        ForEach(viewModel.cards) { card in
                            CardView(card: card, viewModel: viewModel)
                                .aspectRatio(2/3, contentMode: .fit)
                                .onTapGesture {
                                    viewModel.choosedCard(card)
                                }
                        }
                    }
                }
                Spacer()
                HStack
                {
                    Spacer()
                    Text("Score: \(viewModel.getScore())")
                    Spacer()
                }
            }
            .padding()
            .navigationTitle(viewModel.title)
            .toolbar {
                ToolbarItem {
                    Button("New Game")
                    {
                        viewModel.newGame()
                    }
                }
            }
        }
    }

}


struct CardView: View {
    var card : MemorizeModel.Card
    var viewModel : MemorizeViewModel
    
    var body: some View {
        NavigationView {
            ZStack {
                let shape = RoundedRectangle(cornerRadius: 10)
                
                if (card.isFaceUp) {
                    //  One for the inside color
                    shape
                        .fill()
                        .foregroundColor(.white)
                    
                    //  One for the stroke color
                    shape
                        .strokeBorder(lineWidth: 3)
                    Text(card.content)
                        .font(.largeTitle)
                }
                else if(card.isMatched)
                {
                    shape.opacity(0.0)
                }
                else {
                    shape
                        .fill()
                        .foregroundColor(card.color)
                }
            }
            .foregroundColor(card.color)
        }
    }
}

struct ContentView_Previews: PreviewProvider {
    static var previews: some View {
        let game = MemorizeViewModel()
        ContentView(viewModel: game)
            .preferredColorScheme(.dark)
//            .previewInterfaceOrientation(.portraitUpsideDown)
        ContentView(viewModel: game)
    }
}





